import * as Actions from './actions';
import Session from '../imports/session';

const initial_state = {
  name: 'foo',
  teller: null
};

export default function(state = initial_state, action){
  console.log('Reducer >> ', state, action);

  if(action.type == Actions.loginTeller){
    Session.set('teller', action.teller);
    return Object.assign({}, state, {teller: action.teller});
  }

  if(action.type == Actions.logoutTeller){
    Session.set('teller', null);
    return Object.assign({}, state, {teller: null});
  }

  return state;
}
